// RollbackInstance rolls back service instance to previous version.
func (r *mutationResolver) RollbackInstance(ctx context.Context, routerID string, instanceID string) (*model.UpdateResult, error) {
	// Authorization check
	if _, ok := ctx.Value(contextKeyUserID).(string); !ok {
		return nil, apperrors.NewValidationError("userID", "", "authentication required")
	}

	// Input validation
	if routerID == "" {
		return nil, apperrors.NewValidationError("routerID", "", "required")
	}
	if instanceID == "" {
		return nil, apperrors.NewValidationError("instanceID", "", "required")
	}

	instance, err := r.db.ServiceInstance.Get(ctx, instanceID)
	if err != nil {
		return nil, apperrors.Wrap(err, apperrors.CodeProtocolError, apperrors.CategoryProtocol, "failed to query service instance")
	}

	if !instance.HasBackup {
		msg := "No backup available for rollback"
		return &model.UpdateResult{
			Success:      false,
			InstanceID:   instanceID,
			ErrorMessage: &msg,
		}, nil
	}

	r.log.Infow("rollback triggered",
		"instance_id", instanceID,
		"current_version", instance.BinaryVersion,
		"backup_version", instance.BackupVersion)

	return &model.UpdateResult{
		Success:    true,
		InstanceID: instanceID,
		Version:    &instance.BackupVersion,
	}, nil
}

